import { React, Component } from 'react';

import Item from './Item';

class List extends Component {
	constructor(props) {
		super(props);
		this.handleShowContextMenu = this.handleShowContextMenu.bind(this);
		this.handleCloseContextMenu = this.handleCloseContextMenu.bind(this);
	}

	state = {
		top: 0,
		left: 0,
		visibility: 'hidden',
		todo: {
			id: undefined,
			status: undefined,
			name: '',
		},
	};

	handleShowContextMenu(e, todo) {
		this.setState({
			top: e.clientY,
			left: e.clientX,
			visibility: 'visible',
			todo: {
				...this.state.todo,
				...todo,
			},
		});
	}

	handleCloseContextMenu() {
		this.setState({
			...this.state,
			top: 0,
			left: 0,
			visibility: 'hidden',
			todo: {
				id: undefined,
				status: undefined,
				name: '',
			},
		});
	}

	handleSaveStatusTodo(status) {
		this.setState({
			...this.state,
			visibility: 'visible',
		});
		this.props.handleSaveTodo({
			...this.state.todo,
			status: status,
		});
	}

	render() {
		const { todos } = this.props;
		return (
			<>
				<ul>
					{todos.map((todo, key) => {
						return (
							<Item
								todo={todo}
								key={key}
								handlePrepareEdit={this.props.handlePrepareEdit}
								handleDelete={this.props.handleDelete}
								handleShowContextMenu={
									this.handleShowContextMenu
								}
							/>
						);
					})}
				</ul>
				<div
					className={`status-context-menu ${this.state.visibility}`}
					style={{
						top: `${this.state.top}px`,
						left: `${this.state.left}px`,
						transform: `${
							window.innerHeight - this.state.top <= 150
								? 'translateY(-100%)'
								: ''
						}`,
					}}
				>
					<button className={`todo-status todo`}>Todo</button>
					<button className={`todo-status process`}>
						Processing
					</button>
					<button className={`todo-status completed`}>
						Completed
					</button>
				</div>

				<div
					className={`status-context-cover ${this.state.visibility}`}
					onClick={() => {
						this.handleCloseContextMenu();
					}}
				></div>
			</>
		);
	}
}

export default List;
