import { React, Component } from 'react';

class Form extends Component {
	constructor(props) {
		super(props);
	}
	state = {
		id: undefined,
		name: '',
		status: 0,
	};

	handleChange(e) {
		this.setState({
			...this.state,
			name: e.target.value,
		});
	}

	render() {
		return (
			<div className='todo-form'>
				<form
					onSubmit={(e) => {
						e.preventDefault();
						this.props.handleSaveTodo(this.state);
					}}
				>
					<input
						type='text'
						placeholder=' '
						name='name'
						onChange={(e) => {
							this.handleChange(e);
						}}
					/>
					<button
						className='todo-save'
						placeholder='Input todo'
						type='submit'
					>
						SAVE
					</button>
				</form>
			</div>
		);
	}
}

export default Form;
