import { useState } from 'react';
import status from '../constants/status';

import Item from './Item';
const List = ({ todos, handleSaveTodo, handlePrepareEdit, handleDelete }) => {
	const [contextMenu, setContextMenu] = useState({
		top: 0,
		left: 0,
		visibility: 'hidden',
	});

	const [todo, setTodo] = useState({
		id: undefined,
		status: undefined,
		name: '',
	});

	const handleShowContextMenu = (e, todoParam) => {
		setContextMenu({
			top: e.clientY,
			left: e.clientX,
			visibility: 'visible',
		});

		setTodo({ ...todoParam });
	};

	const handleCloseContextMenu = () => {
		setContextMenu({
			...contextMenu,
			visibility: 'hidden',
		});

		setTodo({
			id: undefined,
			status: undefined,
			name: '',
		});
	};

	const handleSaveStatusTodo = (status) => {
		setContextMenu({
			...contextMenu,
			visibility: 'hidden',
		});

		handleSaveTodo({
			...todo,
			status: status,
		});
	};

	return (
		<>
			<ul>
				{todos?.map((todo, key) => {
					return (
						<Item
							todo={todo}
							key={key}
							handlePrepareEdit={handlePrepareEdit}
							handleDelete={handleDelete}
							handleShowContextMenu={handleShowContextMenu}
						/>
					);
				})}
			</ul>
			<div
				className={`status-context-menu ${contextMenu.visibility}`}
				style={{
					top: `${contextMenu.top}px`,
					left: `${contextMenu.left}px`,
					transform: `${
						window.innerHeight - contextMenu.top <= 150
							? 'translateY(-100%)'
							: ''
					}`,
				}}
			>
				<button
					className={`todo-status todo`}
					onClick={() => {
						handleSaveStatusTodo(status.TODO);
					}}
				>
					Todo
				</button>
				<button
					className={`todo-status process`}
					onClick={() => {
						handleSaveStatusTodo(status.PROCESS);
					}}
				>
					Processing
				</button>
				<button
					className={`todo-status completed`}
					onClick={() => {
						handleSaveStatusTodo(status.COMPLETED);
					}}
				>
					Completed
				</button>
			</div>

			<div
				className={`status-context-cover ${contextMenu.visibility}`}
				onClick={() => {
					handleCloseContextMenu();
				}}
			></div>
		</>
	);
};

export default List;
