import { useEffect, useState } from 'react';

const Form = ({ todo }) => {
	const [todoState, setTodoState] = useState({
		id: undefined,
		name: '',
		status: 0,
	});

	useEffect(() => {
		setTodoState({
			...todoState,
			id: this.props.todo.id,
			name: this.props.todo.name,
			status: this.props.todo.status,
		});
	}, [todo]);

	const handleChange = (e) => {
		setTodoState({
			...todo,
			name: e.target.value,
		});
	};

	const handleChangeFormToSave = () => {
		setTodoState({
			id: undefined,
			name: '',
			status: 0,
		});
	};

	return (
		<div className='todo-form'>
			{todoState.id && (
				<button
					className='edit-tag'
					onClick={() => this.handleChangeFormToSave()}
				>
					EDIT: {this.props.todo.name}
				</button>
			)}
			<form
				onSubmit={(e) => {
					e.preventDefault();
					this.props.handleSaveTodo(todoState);
				}}
			>
				<input
					type='text'
					placeholder=' '
					name='name'
					value={todoState.name}
					onChange={(e) => {
						this.handleChange(e);
					}}
				/>
				<button
					className='todo-save'
					placeholder='Input todo'
					type='submit'
				>
					SAVE
				</button>
			</form>
		</div>
	);
};

export default Form;
