import { Component, React } from 'react';
import todoApi from './apis/todoApi';
import './App.scss';
import Form from './components/Form';
import List from './components/List';

class App extends Component {
	constructor(props) {
		super(props);
		this.handleSaveTodo = this.handleSaveTodo.bind(this);
		this.handlePrepareEdit = this.handlePrepareEdit.bind(this);
	}

	initTodo = { id: undefined, name: '', status: undefined };
	state = {
		todos: [this.initTodo],
		todo: this.initTodo,
	};

	renderData() {
		const response = todoApi.GET();
		this.setState({
			...this.state,
			todos: response,
		});
	}

	componentDidMount() {
		this.renderData();
	}

	handleSaveTodo(todo) {
		// truyền todo vào phương thức save
		// kiểm tra lại attribute status nêu không có thì gán là 0
		todoApi.SAVE({ ...todo, status: todo.status ?? 0 });
		// render lại list
		this.renderData({ ...this.initTodo });
	}

	handlePrepareEdit(todo) {
		this.setState({
			...this.state,
			todo: todo,
		});
	}

	render() {
		console.log(this.state);
		return (
			<div className='App'>
				<div className='title'>
					Todo <strong>list</strong>
				</div>
				<div className='todo-list'>
					<Form
						handleSaveTodo={this.handleSaveTodo}
						todo={this.state.todo}
						check={Math.random()}
					/>
					<List
						todos={this.state.todos}
						handlePrepareEdit={this.handlePrepareEdit}
					/>
				</div>
			</div>
		);
	}
}

export default App;
