import { React, Component } from 'react';
import status from '../constants/status';

class List extends Component {
	constructor(props) {
		super(props);
	}
	state = {};
	render() {
		const { todos } = this.props;
		return (
			<ul>
				{todos.map((todo, key) => {
					return (
						<li className='todo-item' key={key}>
							<div className='todo-content'>{todo.name}</div>
							<div
								className={`todo-status ${status.getClass(
									todo.status
								)}`}
							>
								{status.getDisplayName(todo.status)}
							</div>
							<div className='todo-action'>
								<button className='todo-edit'>Edit</button>
								<button className='todo-delete'>Success</button>
							</div>
						</li>
					);
				})}
			</ul>
		);
	}
}

export default List;
