import todoApi from './apis/todoApi';
import './App.scss';
import Form from './components/Form';
import List from './components/List';

const App = () => {
	const initTodo = { id: undefined, name: '', status: undefined };
	const state = {
		todos: [initTodo],
		todo: initTodo,
	};

	const renderData = (todo) => {
		const response = todoApi.GET();
		setState({
			...state,
			todos: response,
			todo: todo ? todo : initTodo,
		});
	};

	const componentDidMount = () => {
		renderData();
	};

	const handleSaveTodo = (todo) => {
		let oldTodo = state.todo;

		todoApi.SAVE({ ...todo, status: todo.status ?? 0 });

		if (todo.id === undefined) {
			todo = { ...initTodo };
		} else if (oldTodo.status != todo.status) {
			todo = { ...initTodo };
		}

		renderData(todo);
	};

	const handlePrepareEdit = (todo) => {
		setState({
			...state,
			todo: todo,
		});
	};

	const handleDelete = (id) => {
		todoApi.DELETE(id);
		renderData();
	};

	return (
		<div className='App'>
			<div className='title'>
				Todo <strong>list</strong>
			</div>
			<div className='todo-list'>
				<Form
					handleSaveTodo={handleSaveTodo}
					todo={state.todo}
					check={Math.random()}
				/>
				<List
					todos={state.todos}
					handlePrepareEdit={handlePrepareEdit}
					handleDelete={handleDelete}
					handleSaveTodo={handleSaveTodo}
				/>
			</div>
		</div>
	);
};

export default App;
