import { useEffect, useState } from 'react';
import todoApi from './apis/todoApi';
import './App.scss';
import Form from './components/Form';
import List from './components/List';

const App = () => {
	const initTodo = { id: undefined, name: '', status: undefined };

	const [todo, setTodo] = useState(initTodo);

	const [todos, setTodos] = useState([initTodo]);

	const renderData = (todoParam) => {
		const response = todoApi.get();

		setTodos([...response]);
		setTodo({ ...todo, ...todoParam });
	};

	useEffect(() => {
		renderData();
	}, []);

	const handleSaveTodo = (todoParam) => {
		let oldTodo = todo;
		todoApi.save({ ...todoParam, status: todoParam.status ?? 0 });

		if (todoParam.id === undefined) {
			todoParam = { ...initTodo };
		} else if (oldTodo.status != todoParam.status) {
			todoParam = { ...initTodo };
		}

		renderData(todoParam);
	};

	const handlePrepareEdit = (todoParam) => {
		setTodo({ ...todoParam });
	};

	const handleDelete = (id) => {
		todoApi.delete(id);
		renderData();
	};

	return (
		<div className='App'>
			<div className='title'>
				Todo <strong>list</strong>
			</div>
			<div className='todo-list'>
				<Form handleSaveTodo={handleSaveTodo} todo={todo} />
				<List
					todos={todos}
					handlePrepareEdit={handlePrepareEdit}
					handleDelete={handleDelete}
					handleSaveTodo={handleSaveTodo}
				/>
			</div>
		</div>
	);
};

export default App;
