import { useEffect, useState } from 'react';
import todoApi from './apis/todoApi';
import './App.scss';
import Form from './components/Form';
import List from './components/List';

const App = () => {
	const initTodo = { id: undefined, name: '', status: undefined };

	const [todo, setTodo] = useState({
		todo: initTodo,
	});

	const [todos, setTodos] = useState({
		todo: [initTodo],
	});

	const renderData = (todoParam) => {
		const response = todoApi.GET();
		setTodos({ ...todos, todos: response });
		setTodo({ ...todo, todo: todoParam ? todoParam : initTodo });
	};

	useEffect(() => {
		renderData();
	}, []);

	const handleSaveTodo = (todoParam) => {
		let oldTodo = state.todo;

		todoApi.save({ ...todoParam, status: todoParam.status ?? 0 });

		if (todoParam.id === undefined) {
			todoParam = { ...initTodo };
		} else if (oldTodo.status != todoParam.status) {
			todoParam = { ...initTodo };
		}

		renderData(todoParam);
	};

	const handlePrepareEdit = (todoParam) => {
		setTodo({ ...todo, todoParam });
	};

	const handleDelete = (id) => {
		todoApi.delete(id);
		renderData();
	};

	return (
		<div className='App'>
			<div className='title'>
				Todo <strong>list</strong>
			</div>
			<div className='todo-list'>
				<Form
					handleSaveTodo={handleSaveTodo}
					todo={state.todo}
					check={Math.random()}
				/>
				<List
					todos={state.todos}
					handlePrepareEdit={handlePrepareEdit}
					handleDelete={handleDelete}
					handleSaveTodo={handleSaveTodo}
				/>
			</div>
		</div>
	);
};

export default App;
