import { React, Component, useState } from 'react';
import status from '../constants/status';

import Item from './Item';
const List = ({ todos, handleSaveTodo }) => {
	const [contextMenu, setContextMenu] = useState({
		top: 0,
		left: 0,
		visibility: 'hidden',
	});

	const [todo, setTodo] = useState({
		id: undefined,
		status: undefined,
		name: '',
	});

	const handleShowContextMenu = (e, todoParam) => {
		setContextMenu({
			top: e.clientY,
			left: e.clientX,
			visibility: 'visible',
		});

		setTodo(...todoParam);
	};

	const handleCloseContextMenu = () => {
		setContextMenu({
			...contextMenu,
			visibility: 'hidden',
		});

		setTodo({
			id: undefined,
			status: undefined,
			name: '',
		});
	};

	const handleSaveStatusTodo = (status) => {
		this.setState({
			...this.state,
			visibility: 'hidden',
		});
		handleSaveTodo({
			...this.state.todo,
			status: status,
		});
	};

	return (
		<>
			<ul>
				{todos?.map((todo, key) => {
					return (
						<Item
							todo={todo}
							key={key}
							handlePrepareEdit={this.props.handlePrepareEdit}
							handleDelete={this.props.handleDelete}
							handleShowContextMenu={this.handleShowContextMenu}
						/>
					);
				})}
			</ul>
			<div
				className={`status-context-menu ${this.state.visibility}`}
				style={{
					top: `${this.state.top}px`,
					left: `${this.state.left}px`,
					transform: `${
						window.innerHeight - this.state.top <= 150
							? 'translateY(-100%)'
							: ''
					}`,
				}}
			>
				<button
					className={`todo-status todo`}
					onClick={() => {
						this.handleSaveStatusTodo(status.TODO);
					}}
				>
					Todo
				</button>
				<button
					className={`todo-status process`}
					onClick={() => {
						this.handleSaveStatusTodo(status.PROCESS);
					}}
				>
					Processing
				</button>
				<button
					className={`todo-status completed`}
					onClick={() => {
						this.handleSaveStatusTodo(status.COMPLETED);
					}}
				>
					Completed
				</button>
			</div>

			<div
				className={`status-context-cover ${this.state.visibility}`}
				onClick={() => {
					this.handleCloseContextMenu();
				}}
			></div>
		</>
	);
};

export default List;
